.bs h3{
    text-align: left;
    font-size: 22px;
    margin-top: 0;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}
.bs h3:hover{
    color: stencilColor("color-textLink--hover");
}
.author svg{
    display: inline-block;
}
.date-time{
    font-size: 14px;
    font-weight: 400;
    text-transform: capitalize;
    margin-bottom: 5px;
}
.blog-summary p{
    font-size: 16px;
    line-height: 30px;
    color:stencilColor("color-textSecondary");
    margin: 0 0 15px 0;
}
.webi_post_hover {
    opacity: 0;
    visibility: hidden;
    z-index: 0;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    transition: .4s ease;
    -webkit-transform: scale(.8);
    background-color: rgba(255,255,255,.4);
    padding: 10px;
    border-radius: 0px;
}
.blog-left:hover .webi_post_hover {
    opacity: 1;
    visibility: visible;
    transform: scale(.9,.9);
}
.blog-left {
    position: relative;
}
.blog-ic {
    position: absolute;
    left: 0;
    right: 0;
    text-align: center;
    top: 50%;
}
.webi_post_hover .icon {
    border-radius: 0px;
    transform: translate(-20%,-50%);
    padding: 12px 12px;
    display: inline-block;
    line-height: 0;
    background: stencilColor("button--primary-backgroundColor");
    color: #fff;
    width: 36px;
    height: 36px;
    border: none;
}
.webi_post_hover .icon:hover {
    background-color: stencilColor("button--primary-backgroundColorHover");
    color: stencilColor("button--primary-colorHover");
}
.single-blog .socialLinks .icon svg {
    background-color: stencilColor("primary-background-color");
    fill: white;
    height: 32px;
    width: 32px;
    padding: 9px;
    border-radius: 0px;
    margin: 0;
}
.single-blog .socialLinks-item:hover .icon {
    background: 0 0;
}
.blog {
    margin-bottom: spacing("single");

    @include breakpoint("medium") {
        margin: auto;
        width: 100%;
    }

    + .blog {
        border-top: container("border");
        margin-top: spacing("double");
        padding-top: spacing("double");
    }

    .tags {
        margin-top: 1.5rem;
        text-align: left;
    }

    .socialLinks {
        text-align: left;
        margin: 15px 0px;
    }

    .addthis_toolbox {
        margin-top: spacing("double");
    }
}

.blog-title {
    font-size: fontSize("large");
    margin: 0 0 1rem;

    a {
        text-decoration: none;
    }
}

.blog-date,
.blog-author {
    color: stencilColor("color-textBase");
    text-align: center; 
    margin-bottom: 7px;
    display: inline-block;

}

.blog-author {
    margin-bottom: spacing("single") + spacing("base");
}

.blog-post {
    font-size: 14px;
    text-align: left;
    line-height: 25px;
    color: stencilColor("color-textLink--active");
}
.blog-post a {
    text-transform: capitalize;
    font-weight: 600;
}
.allblog {
    margin-bottom: 30px;
    padding: 0;
}
.blog-thumbnail {
    margin: 0 auto (spacing("base") * 1);
    max-width: 100%;
    position: sticky;
    top: 0;

    img {
        width: 100%;
        border-radius: 0px;
    }
}
.blog-title:hover{
    color: stencilColor("color-textBase--hover");
}
.blog-post-figure {
