    width: 100%;

    @include breakpoint("small") {
        
    }

    @include breakpoint("medium") {
        
    }

    @include breakpoint("large") {
        
    }
}
.blog-header h2 {
    margin-bottom: 10px !important;
    text-transform: capitalize;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
}
.allcomment h2 {
    font-weight: 600;
    font-size: 18px;
    margin: 0;
    line-height: 23px;
}
.single-blog {
    margin-top: 20px;
}
.single-blog .blog-title {
    text-overflow: unset;
    white-space: unset;
    overflow: unset;
}
.blog-header p {
    margin-bottom: 10px;
    text-align: left;
}
.tag {
    display: inline-block;
    font-size: 1rem;
    background-color: stencilColor("primary-background");
    border-radius: 0px;
    margin-right: 5px;
}
.blog-post-body .socialLinks .icon svg {
    background-color: stencilColor("color-secondary-background");
}
.blog-post-body .socialLinks .icon:hover svg {
