@media (min-width: 1200px) and (max-width: 1409px){
    .allblog-bg .blog-right{
        padding: 15px 15px;
    }
}
@media (min-width: 801px){
    .sing-blog {
        padding: 0;
    }
}    
@media(max-width: 1199px){
    
    .webi-blog-image img {
        border-radius: 0px;
    }
}
@media(max-width: 991px){
    .bs h3{
        font-size: 18px;
    }
    .blog-summary p{
        font-size: 14px;
        line-height: 20px;
        height: 20px;
        overflow: hidden;
    }
}
@media(max-width: 768px){
    .bs h4{
        font-size: 18px;
    }
    .date-time{
        font-size: 14px;
    }
    .blog-left, .blog-right{
        -webkit-box-flex: 0;
        -ms-flex: 0 0 100%;
        flex: 0 0 100%;
        max-width: 100%;
        padding:0;
    }
    .allblog-bg .blog-right{
        position: unset;
        max-width: 100%;
        bottom: 0;
        border-radius: 0px;
        padding: 25px 0px 0px 0px;
    }
    .blogshadow{
        margin: 0;
    }
    .blog-summary p{
        height: 100%;
    }
}
@media(max-width: 600px){
    .blog-title{
        font-size: 18px;
