.blog-summary p{
    font-size: 16px;
    line-height: 30px;
    color:stencilColor("color-textSecondary");
    margin: 0 0 15px 0;
}
.webi_post_hover {
    opacity: 0;
    visibility: hidden;
    z-index: 0;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    transition: .4s ease;
    -webkit-transform: scale(.8);
    background-color: rgba(255,255,255,.4);
    padding: 10px;
    border-radius: 0px;
}
.blog-left:hover .webi_post_hover {
    opacity: 1;
    visibility: visible;
    transform: scale(.9,.9);
}
.blog-left {
    position: relative;
}
.blog-ic {
    position: absolute;
    left: 0;
    right: 0;
    text-align: center;
    top: 50%;
}
.webi_post_hover .icon {
    border-radius: 0px;
    transform: translate(-20%,-50%);
    padding: 12px 12px;
    display: inline-block;
    line-height: 0;
    background: stencilColor("button--primary-backgroundColor");
    color: #fff;
    width: 36px;
    height: 36px;
    border: none;
}
.webi_post_hover .icon:hover {
    background-color: stencilColor("button--primary-backgroundColorHover");
    color: stencilColor("button--primary-colorHover");
}
.single-blog .socialLinks .icon svg {
    background-color: stencilColor("primary-background-color");
    fill: white;
    height: 32px;
    width: 32px;
    padding: 9px;
    border-radius: 0px;
    margin: 0;
}
.single-blog .socialLinks-item:hover .icon {
    background: 0 0;
}
